import '../styles/contact.css';
import { useState } from 'react';
import React, { Component }  from 'react';
import emailjs from 'emailjs-com'

export default function Contact() {
    const [firstName, setFName] = useState("");
    const [lastName, setLName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [emailSent, setEmailSent] = useState(false);
    function encode(data) {
        return Object.keys(data)
          .map(
            (key) => encodeURIComponent(key) + "=" + encodeURIComponent(data[key])
          )
          .join("&");
      }

      function handleSubmit(e) {
        e.preventDefault();
        fetch("/", {
          method: "POST",
          headers: { "Content-Type": "application/x-www-form-urlencoded" },
          body: encode({ "form-name": "contact", firstName, lastName, email, message }),
        })
          .then(() => alert("Message sent!"))
          .catch((error) => alert(error));
      }
      function isValidEmail (email) {
        const regex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return regex.test(String(email).toLowerCase());
       };
      function submit () {
        console.log("entered submit function")
         if (firstName && lastName && isValidEmail(email) && message) {

            const serviceId = 'service_4r7qrye';
            const templateId = 'template_s3wy4qg';
            const publicKey = 'sPy2scEgxsHAGbk__';
            const templateParams = {
                firstName,
                lastName,
                email,
                message
            };
            console.log(email)
            // emailjs.send(serviceId, templateId, templateParams, user_id)
            //     .then(response => console.log(response))
            //     .then(error => console.log(error));
            emailjs.send(serviceId, templateId, templateParams, publicKey)
                .then(function(response) {
                console.log('SUCCESS!', response.status, response.text);
                }, function(error) {
                console.log('FAILED...', error);
                });
            console.log("MAYBE SENT EMAIL")
            // setFName('');
            // setLName('');
            // setEmail('');
            // setMessage('');
            setEmailSent(true);
            if (emailSent===true){
                alert('Your email was successfully sent. We will be in contact soon!')
            }
        } else {
            alert('Please fill in all fields.');
        }
        console.log("NOTHING")
    }


    return(
        <section className="contact-section">
            <h2>Contact Me</h2>

            <div>
                <div className='contact-me-personal-info'>
                    <a />
                </div>
                <form  name="contact" className="contact-container-form">
                    <div className='contact-name'>
                    <label className='contact-form-attribute'>
                        First Name:
                        <input 
                        value={firstName}
                        className='input-text' type="text" 
                        onChange={(e) => setFName(e.target.value)}/>
                    </label>
                    <label className='contact-form-attribute'>
                        Last Name:
                        <input 
                        value={lastName}
                        className='input-text' type="text" 
                        onChange={(e) => setLName(e.target.value)}/>

                    </label>
                    </div>
                    <label className='contact-form-attribute'>
                        Your Email:
                        <input 
                        value={email}
                        className='input-text' type="text" 
                        onChange={(e) => setEmail(e.target.value)}/>
                    </label>
                    <label className='contact-form-attribute'>
                        Your Message:
                        <textarea  value={message} className='message-box' 
                                    onChange={(e) => setMessage(e.target.value)}/>
                    </label>
                    <button onClick={submit} className='contact-submit' type="submit" >Submit</button>
                </form>

            </div>
            <hr/>

        </section>
    );
}
